mapper.select:
	SELECT	* FROM TBL_QUESTION
	WHERE 1=1
	
		AND TYPE = #{type}
	
	
		AND CATEGORY  = #{category}
	
	
	    
    		 AND ${SEARCH_TYPE} LIKE '%${SEARCH_TEXT}%'
    	
    
    
    	order by ${UP} asc
    
    
    	order by ${DOWN} desc
    
    
    	order by CREATE_TM desc
    
    LIMIT ${LIMIT} OFFSET ${OFFSET}

SELECT	COUNT(*) FROM TBL_QUESTION
WHERE 1=1

	AND TYPE = #{type}
