mapper.select:
	SELECT /*UserNoticesMapper*/ * FROM TBL_NOTICES
	WHERE 1=1 
	
    	AND MEMBER_ID = #{MEMBER_ID}
    
    
	order by UPDATE_TM desc
	
	LIMIT ${LIMIT} OFFSET ${OFFSET}

	SELECT /*UserNoticesMapper*/ count(*) FROM TBL_NOTICES
	WHERE 1=1 
	
    	AND "MEMBER_ID" = #{MEMBER_ID}

mapper.insert:
INSERT INTO /*UserNoticesMapper*/ TBL_NOTICES ( IDX, NOTICES, TYPE, CREATE_TM, UPDATE_TM
) VALUES ( #{IDX}, #{NOTICES}, #{TYPE}, now(), now()
)
