   	ORDER BY CREATE_TM DESC
    LIMIT ${LIMIT} OFFSET ${OFFSET}

SELECT COUNT(*) FROM TBL_SUBMISSION WHERE 1=1
AND ${SEARCH_TYPE} LIKE '%${SEARCH_TEXT}%'
AND AWARD_IDX = #{award_idx}

SELECT detail.* , requires.require_name , requires.require_idx FROM TBL_SUBMISSION_DETAIL detail JOIN TBL_AWARD_REQUIRE requires ON detail.require_idx = requires.idx
WHERE detail.SUBMISSION_IDX = #{idx}
ORDER BY detail.CREATE_TM

