mapper.select:
/*UserPortfolioMapper*/ SELECT A.* ,B.PROFESSOR_MEMBER_ID ,B.SUPPORT_GROUP ,C.NAME ,C.SCHOOL_NAME ,C.SCHOOL_TYPE ,C.SCHOOL_YEAR ,C.SCHOOL_GROUP ,C.ADDRESS_LOCAL ,C.SEX ,C.SUPPORT_AREA ,D.NAME AS PROF_NAME ,IFNULL((SELECT NAME FROM TBL_PROGRAM WHERE IDX = A.PROGRAM_IDX),'') AS PROGRAM_NAME ,DATE_FORMAT(TIMEDIFF(END_TM, START_TM),'%H') AS PERIOD FROM TBL_PORTFOLIO A LEFT OUTER JOIN TBL_MATCHING B ON B.MEMBER_ID = A.MEMBER_ID LEFT OUTER JOIN TBL_MEMBER C ON C.MEMBER_ID = A.MEMBER_ID LEFT OUTER JOIN TBL_MEMBER D ON D.MEMBER_ID = B.PROFESSOR_MEMBER_ID WHERE 1=1
AND A.MEMBER_ID = #{MEMBER_ID}
AND B.YEAR = #{YEAR} AND A.YEAR = #{YEAR}
ORDER BY A.START_TM DESC LIMIT ${LIMIT} OFFSET ${OFFSET}

/*UserPortfolioMapper*/ SELECT SUM(CASE COURSE WHEN 1 THEN RTICIPATION_TM ELSE 0 END) AS SUM_1 ,SUM(CASE COURSE WHEN 2 THEN RTICIPATION_TM ELSE 0 END) AS SUM_2 ,SUM(CASE COURSE WHEN 3 THEN RTICIPATION_TM ELSE 0 END) AS SUM_3 ,SUM(CASE COURSE WHEN 4 THEN RTICIPATION_TM ELSE 0 END) AS SUM_4 ,SUM(RTICIPATION_TM) AS SUM_A ,ROUND(AVG(STFT),2) AS AVG_A FROM TBL_PORTFOLIO WHERE MEMBER_ID = #{MEMBER_ID}
AND YEAR = #{YEAR}
AND COURSE IS NOT NULL

	/*UserPortfolioMapper*/
	SELECT COUNT(*) 
	  FROM TBL_PORTFOLIO A
	 WHERE 1=1 
	
    	AND A.MEMBER_ID = #{MEMBER_ID}
    
    
    	AND A.YEAR = #{YEAR}

/*UserPortfolioMapper*/ SELECT A.* ,B.PROFESSOR_MEMBER_ID ,B.SUPPORT_GROUP ,C.NAME ,C.SCHOOL_NAME ,C.SCHOOL_TYPE ,C.SCHOOL_YEAR ,C.SCHOOL_GROUP ,C.ADDRESS_LOCAL ,C.SEX ,C.SUPPORT_AREA ,D.NAME AS PROF_NAME FROM TBL_PORTFOLIO A LEFT OUTER JOIN TBL_MATCHING B ON B.MEMBER_ID = A.MEMBER_ID LEFT OUTER JOIN TBL_MEMBER C ON C.MEMBER_ID = A.MEMBER_ID LEFT OUTER JOIN TBL_MEMBER D ON D.MEMBER_ID = B.PROFESSOR_MEMBER_ID WHERE IDX = #{IDX}
AND A.YEAR = #{YEAR} AND B.YEAR = #{YEAR}
;

mapper.update:
/*UserPortfolioMapper*/
UPDATE TBL_PORTFOLIO 
   SET START_TM           = #{START_TM} 
	   ,END_TM            = #{END_TM}
	   ,RTICIPATION_TM    = #{RTICIPATION_TM}
	   ,PLACE             = #{PLACE}
	   ,PRGM_NM           = #{PRGM_NM}
	   ,EVALUATION        = #{EVALUATION}
	   ,STFT              = #{STFT}
	   ,COURSE            = #{COURSE}
	   ,COURSE_PRGM       = #{COURSE_PRGM}
	
	   ,EVAL_OPEN_YN      = #{EVAL_OPEN_YN}
	
	
		,PROF_STFT         = #{PROF_STFT}
	
	
		,EVALUATION_TACHER = #{EVALUATION_TACHER}
		   
	
	   ,FILES= #{FILES}
	
	
	   ,UPDATE_TM= now() 
 WHERE IDX = #{IDX}
